package Generique.gameplay.ai;import Generique.moteur.ai.AI;import Generique.moteur.core_kernel.Entity;import Generique.gameplay.physics.Displacement;public class RandomAI implements AI {    public RandomAI(){}    public void update(Entity entity){        Displacement[] possibleDisplacement = new Displacement[]{Displacement.RIGHT, Displacement.LEFT, Displacement.UP, Displacement.DOWN, Displacement.NOTHING};        int choose = (int) (Math.random()*possibleDisplacement.length);        entity.setOrientation(possibleDisplacement[choose].orientation);    }}