package Generique.gameplay.ai;import Generique.Moteur.ai.AI;import Generique.gameplay.physics.Displacement;public class RandomAI implements AI {    public RandomAI(){}    public double move(){        Displacement[] possibleDisplacement = new Displacement[]{Displacement.RIGHT, Displacement.LEFT, Displacement.UP, Displacement.DOWN, Displacement.NOTHING};        int choose = (int) (Math.random()*possibleDisplacement.length);        return possibleDisplacement[choose].orientation;    }}