package gameplay.ai;import moteur.ai.AI;import moteur.core_kernel.Entity;import gameplay.physics.Displacement;/** * Classe definissant l'ia aleatoire */public class RandomAI implements AI {    public RandomAI(){}    /**     * methode definissant le prochain deplacement de l'entity passé en paramètre     * @param entity     */    public void update(Entity entity){        Displacement[] possibleDisplacement = new Displacement[]{Displacement.RIGHT, Displacement.LEFT, Displacement.UP, Displacement.DOWN, Displacement.NOTHING};        int choose = (int) (Math.random()*possibleDisplacement.length);        if(possibleDisplacement[choose] != Displacement.NOTHING)            entity.setOrientation(possibleDisplacement[choose].orientation);    }}