package gameplay.ai;import gameplay.model.GameModel;import moteur.ai.AI;import moteur.core_kernel.Entity;import gameplay.physics.Displacement;/** * Classe definissant l'ia aleatoire */public class RandomAI implements AI {    private Displacement[] possibleDisplacement;    public RandomAI(){        possibleDisplacement = new Displacement[]{Displacement.RIGHT, Displacement.LEFT, Displacement.UP, Displacement.DOWN, Displacement.NOTHING};    }    /**     * methode definissant le prochain deplacement de l'entity passé en paramètre     * @param entity     */    public void update(Entity entity){        if(! canChangeDirection(entity)) return;        int choose = (int) (Math.random()*possibleDisplacement.length);        if(possibleDisplacement[choose] != Displacement.NOTHING)            entity.setOrientation(possibleDisplacement[choose].orientation);    }    /**     * fonction permettant de detecter si le changement de direction est possible     * @return     */    private boolean canChangeDirection(Entity entity){        return (entity.getPosition().getX() % GameModel.getInstance().getMap().getDimCellWdt() == 0)            && (entity.getPosition().getY() % GameModel.getInstance().getMap().getDimCellHgt() == 0);    }}