package moteurs;import moteurs.physics.Collider;public abstract class Entity {    private Position position;    private Collider collider;    public Entity(Position position, Collider collider){        this.position = position;        this.collider = collider;    }    public void move(Position nextPosition){        collider.move(nextPosition);        position.setX(nextPosition.getX());        position.setY(nextPosition.getY());    }    public Position getPosition() {        return position;    }    public Collider getCollider(){        return collider;    }}