package moteurs;import moteurs.physics.Collider;public abstract class Entity {    private Position position;    private Collider collider;    private String name;    public Entity(Position position, Collider collider, String name){        this.position = position;        this.collider = collider;        this.name = name;    }    public void move(Position nextPosition){        collider.move(nextPosition);        position.setX(nextPosition.getX());        position.setY(nextPosition.getY());    }    public Position getPosition() {        return position;    }    public Collider getCollider(){        return collider;    }    public String getName() {        return name;    }}